    private void jbCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCalcularActionPerformed
        // TODO add your handling code here:
        // cc = new mNeville();
        //cc.dosDatos();

        String xx0 = tfx0.getText();
        String xx1 = tfx1.getText();
        String xx2 = tfx2.getText();


        String fxx0 = tffx0.getText();
        String fxx1 = tffx1.getText();
        String fxx2 = tffx2.getText();


        float x0 = Float.parseFloat(xx0);
        float x1 = Float.parseFloat(xx1);
        float x2 = Float.parseFloat(xx2);


        float fx0 = Float.parseFloat(fxx0);
        float fx1 = Float.parseFloat(fxx1);
        float fx2 = Float.parseFloat(fxx2);
 

        //Para 2  (0,1)
        float a = fx1;
        float b = (-x0) * fx1 ;
        float c = fx0;
        float d = (-x1) * fx0;
        float e = x1 - x0;

        float f = (a - c) / e;
        float g = (b - d) / e;

        System.out.println("El Resultado es:" + f + "x" +" "+"+"+" "+ g);
        //

        //Para 2 (1,2)

        float t1 = fx2;
        float t2 = (-x1) * fx2;
        float t3 = fx1;
        float t4 = (-x2) * fx1;
        float t5 = x2 - x1;

        float t6 = (t1 - t3) / t5;
        float t7 = (t2 - t4) / t5;

        System.out.println("El Resultado es:" + t6 + "x" +" "+"+"+" "+ t7);



        //Para 3 (0,1,2)

        float aa = t6;
        float bb= (-x0) * t6;
        float cc= t7;
        float dd= (-x0) * t7;
        float ee= f;
        float ff= (-x2) * f;
        float gg= g;
        float hh= (-x2) * g;
        float ii= x2 - x0;

        float jj= (aa - ee) / ii;
        float kk= ((bb+cc)-(ff+gg)) / ii;
        float ll= (dd - hh) / ii;

        System.out.println("El Resultado es:" + jj + "xÂ²" +" "+"+"+" "+ kk + "x"+" "+"+"+" "+ ll);



    }//GEN-LAST:event_jbCalcularActionPerformed

